import React, { useState, useEffect, useRef } from 'react';
import { BrowserRouter as Router, Routes, Route, useNavigate, Navigate } from 'react-router-dom';
import Login from './components/Login';
import Register from './components/Register';
import './App.css';
import axios from 'axios';

function QuizPage() {
  const [questions, setQuestions] = useState([]);
  const [current, setCurrent] = useState(0);
  const [score, setScore] = useState(0);
  const [answered, setAnswered] = useState([]);
  const [selected, setSelected] = useState([]);
  const [timeLeft, setTimeLeft] = useState(15 * 60); // 15 minutes
  const timerRef = useRef();
  const navigate = useNavigate();

  useEffect(() => {
    // Redirect to login if not logged in
    if (!localStorage.getItem('username')) {
      navigate('/login');
      return;
    }
    // Fetch questions from backend
    axios.get('http://localhost:5000/api/questions?limit=50')
      .then(res => {
        setQuestions(res.data);
        setAnswered(Array(res.data.length).fill(false));
        setSelected(Array(res.data.length).fill(null));
      })
      .catch(() => {
        setQuestions([]);
      });
  }, [navigate]);

  useEffect(() => {
    timerRef.current = setInterval(() => {
      setTimeLeft((prev) => {
        if (prev <= 1) {
          clearInterval(timerRef.current);
          return 0;
        }
        return prev - 1;
      });
    }, 1000);
    return () => clearInterval(timerRef.current);
  }, []);

  if (!localStorage.getItem('username')) return null;
  if (!questions.length) return <div className="container"><h2>Loading questions...</h2></div>;

  const totalQuestions = questions.length;
  const q = questions[current];
  const userAnswered = answered[current];
  const userSelected = selected[current];

  const handleAnswer = (option) => {
    if (userAnswered) return;
    const newAnswered = [...answered];
    newAnswered[current] = true;
    setAnswered(newAnswered);
    const newSelected = [...selected];
    newSelected[current] = option;
    setSelected(newSelected);
    if (option === q.correctAnswer) {
      setScore((prev) => prev + 1);
    }
  };

  const prevQuestion = () => {
    if (current > 0) setCurrent(current - 1);
  };
  const nextQuestion = () => {
    if (current < totalQuestions - 1) setCurrent(current + 1);
  };

  const formatTime = (t) => `${Math.floor(t / 60)}:${(t % 60).toString().padStart(2, '0')}`;

  return (
    <div className="container" id="quizContainer">
      <h1>General Knowledge Quiz</h1>
      <div id="timer">Time Left: {formatTime(timeLeft)}</div>
      <div id="question" className="question">{current + 1}. {q.question}</div>
      <div id="options" className="options">
        {q.options.map((option, idx) => {
          let btnClass = '';
          if (userAnswered) {
            if (option === q.correctAnswer) btnClass = 'correct';
            else if (option === userSelected && option !== q.correctAnswer) btnClass = 'wrong';
          }
          return (
            <button
              key={idx}
              onClick={() => handleAnswer(option)}
              disabled={userAnswered}
              className={btnClass}
            >
              {option}
            </button>
          );
        })}
        {userAnswered && userSelected !== q.correctAnswer && (
          <p className="feedback">The correct answer was: {q.correctAnswer}</p>
        )}
      </div>
      <div className="navigation">
        <button className="nav-btn" onClick={prevQuestion} disabled={current === 0}>Previous</button>
        <button className="nav-btn" onClick={nextQuestion} disabled={current === totalQuestions - 1}>Next</button>
      </div>
      <div id="score" className="score">Score: {score}/{totalQuestions}</div>
      <div className="progress-bar"><div className="progress" id="progress" style={{ width: `${((current + 1) / totalQuestions) * 100}%` }}></div></div>
    </div>
  );
}

function RequireAuth({ children }) {
  if (!localStorage.getItem('username')) {
    return <Navigate to="/login" replace />;
  }
  return children;
}

function App() {
  return (
    <Router>
      <Routes>
        <Route path="/login" element={<Login />} />
        <Route path="/register" element={<Register />} />
        <Route path="/quiz" element={<RequireAuth><QuizPage /></RequireAuth>} />
        <Route path="*" element={<Navigate to="/quiz" replace />} />
      </Routes>
    </Router>
  );
}

export default App;
