import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function Results() {
  const [score, setScore] = useState(0);
  const [total, setTotal] = useState(0);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const checkAnswers = async () => {
      try {
        const token = localStorage.getItem('token');
        const answers = JSON.parse(localStorage.getItem('quizAnswers')) || [];
        let correct = 0;
        for (const ans of answers) {
          const res = await axios.post('http://localhost:5000/api/questions/submit', {
            questionId: ans.questionId,
            answer: ans.answer
          }, {
            headers: { Authorization: `Bearer ${token}` }
          });
          if (res.data.isCorrect) correct++;
        }
        setScore(correct);
        setTotal(answers.length);
        setLoading(false);
      } catch (err) {
        setError('Failed to calculate results.');
        setLoading(false);
      }
    };
    checkAnswers();
  }, []);

  if (loading) return <div>Calculating results...</div>;
  if (error) return <div style={{ color: 'red' }}>{error}</div>;

  return (
    <div>
      <h2>Quiz Results</h2>
      <p>Your score: {score} out of {total}</p>
      <button onClick={() => navigate('/')}>Go Home</button>
    </div>
  );
}

export default Results;
