import React, { useEffect, useState, useRef } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './Quiz.css';
import rightSoundFile from '../assets/right.mp3';
import wrongSoundFile from '../assets/wrong.mp3';

function Quiz() {
  const [questions, setQuestions] = useState([]);
  const [current, setCurrent] = useState(0);
  const [answers, setAnswers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [timeLeft, setTimeLeft] = useState(15 * 60); // 15 minutes in seconds
  const [animating, setAnimating] = useState(false);
  const [showCorrect, setShowCorrect] = useState(false);
  const [lastCorrectAnswer, setLastCorrectAnswer] = useState('');
  const [lastWasCorrect, setLastWasCorrect] = useState(null); // null, true, or false
  const [answerLocked, setAnswerLocked] = useState(false);
  const timerRef = useRef();
  const navigate = useNavigate();

  useEffect(() => {
    const fetchQuestions = async () => {
      try {
        const token = localStorage.getItem('token');
        const res = await axios.get('http://localhost:5000/api/questions?limit=50', {
          headers: { Authorization: `Bearer ${token}` }
        });
        setQuestions(res.data);
        setLoading(false);
      } catch (err) {
        setError('Failed to load questions.');
        setLoading(false);
      }
    };
    fetchQuestions();
  }, []);

  // Timer logic
  useEffect(() => {
    if (loading) return;
    timerRef.current = setInterval(() => {
      setTimeLeft((prev) => {
        if (prev <= 1) {
          clearInterval(timerRef.current);
          handleSubmit();
          return 0;
        }
        return prev - 1;
      });
    }, 1000);
    return () => clearInterval(timerRef.current);
    // eslint-disable-next-line
  }, [loading]);

  const handleAnswer = (answer) => {
    if (answerLocked) return;
    setAnswerLocked(true);
    const isCorrect = answer === questions[current].correctAnswer;
    setLastWasCorrect(isCorrect);
    setShowCorrect(true);
    setLastCorrectAnswer(questions[current].correctAnswer);
    const newAnswers = [...answers];
    newAnswers[current] = { questionId: questions[current]._id, answer };
    setAnswers(newAnswers);
    // Play sound
    const rightSound = new window.Audio(rightSoundFile);
    const wrongSound = new window.Audio(wrongSoundFile);
    if (isCorrect) rightSound.play();
    else wrongSound.play();
    // Move to next after delay
    setTimeout(() => {
      setShowCorrect(false);
      setAnswerLocked(false);
      setLastWasCorrect(null);
      if (current < questions.length - 1) {
        handleNext();
      } else {
        handleSubmit();
      }
    }, 1500);
  };

  const handleNext = () => {
    if (current < questions.length - 1) {
      setAnimating(true);
      setTimeout(() => {
        setCurrent(current + 1);
        setAnimating(false);
      }, 300);
    }
  };

  const handlePrev = () => {
    if (current > 0) {
      setAnimating(true);
      setTimeout(() => {
        setCurrent(current - 1);
        setAnimating(false);
      }, 300);
    }
  };

  const handleSubmit = () => {
    localStorage.setItem('quizAnswers', JSON.stringify(answers));
    navigate('/results');
  };

  if (loading) return <div>Loading questions...</div>;
  if (error) return <div style={{ color: 'red' }}>{error}</div>;
  if (!questions.length) return <div>No questions found.</div>;

  const q = questions[current];
  const minutes = Math.floor(timeLeft / 60);
  const seconds = timeLeft % 60;

  return (
    <div>
      {/* --- PROMINENT TIMER BAR --- */}
      <div style={{ width: '100%', display: 'flex', justifyContent: 'center', alignItems: 'center', margin: '24px 0' }}>
        <div style={{
          fontWeight: 'bold',
          fontSize: 36,
          color: '#fff',
          background: timeLeft < 60 ? '#f44336' : '#2575fc',
          borderRadius: 12,
          padding: '16px 48px',
          boxShadow: '0 4px 16px #0002',
          letterSpacing: 2,
          border: '4px solid #fff',
          minWidth: 320,
          textAlign: 'center',
          transition: 'background 0.3s, color 0.3s'
        }}>
          ‚è∞ Time Left: {minutes}:{seconds.toString().padStart(2, '0')}
        </div>
      </div>
      <div className={`quiz-question-anim ${animating ? 'fade' : ''}`}>
        <div style={{ margin: '24px 0' }}>{q.question}</div>
        <div>
          {q.options && q.options.map((opt, idx) => (
            <button
              key={idx}
              className={
                answers[current]?.answer === opt
                  ? lastWasCorrect === false && opt === q.correctAnswer
                    ? 'correct'
                    : lastWasCorrect === false && answers[current]?.answer === opt
                      ? 'incorrect'
                      : lastWasCorrect === true && answers[current]?.answer === opt
                        ? 'correct'
                        : 'selected'
                  : ''
              }
              onClick={() => handleAnswer(opt)}
              style={{
                display: 'block',
                margin: '8px 0',
                width: '100%',
                background: answers[current]?.answer === opt
                  ? lastWasCorrect === false && opt === q.correctAnswer
                    ? '#4caf50'
                    : lastWasCorrect === false && answers[current]?.answer === opt
                      ? '#f44336'
                      : lastWasCorrect === true && answers[current]?.answer === opt
                        ? '#4caf50'
                        : '#2575fc'
                  : '#fff',
                color: answers[current]?.answer === opt ? '#fff' : '#222',
                fontWeight: answers[current]?.answer === opt ? 'bold' : 'normal',
                border: answers[current]?.answer === opt
                  ? lastWasCorrect === false && opt === q.correctAnswer
                    ? '3px solid #4caf50'
                    : lastWasCorrect === false && answers[current]?.answer === opt
                      ? '3px solid #f44336'
                      : lastWasCorrect === true && answers[current]?.answer === opt
                        ? '3px solid #4caf50'
                        : '2px solid #2575fc'
                  : '1px solid #ccc',
                borderRadius: 8,
                fontSize: 20,
                transition: 'background 0.3s, border 0.3s, color 0.3s'
              }}
              disabled={answerLocked}
            >
              {opt}
            </button>
          ))}
        </div>
        {showCorrect && lastWasCorrect === false && (
          <div style={{ color: 'green', marginTop: 8 }}>
            Correct answer: {lastCorrectAnswer}
          </div>
        )}
        {showCorrect && lastWasCorrect === true && (
          <div style={{ color: 'blue', marginTop: 8 }}>
            Correct!
          </div>
        )}
      </div>
      <div style={{ marginTop: 24, display: 'flex', justifyContent: 'space-between' }}>
        <button onClick={handlePrev} disabled={current === 0 || answerLocked}>Previous</button>
        {current < questions.length - 1 ? (
          <button onClick={handleNext} disabled={typeof answers[current] === 'undefined' || answerLocked}>Next</button>
        ) : (
          <button onClick={handleSubmit} disabled={answers.length !== questions.length || answerLocked}>Submit</button>
        )}
      </div>
    </div>
  );
}

export default Quiz;
