import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function Quiz() {
  const [questions, setQuestions] = useState([]);
  const [current, setCurrent] = useState(0);
  const [answers, setAnswers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const fetchQuestions = async () => {
      try {
        const token = localStorage.getItem('token');
        const res = await axios.get('http://localhost:5000/api/questions?limit=50', {
          headers: { Authorization: `Bearer ${token}` }
        });
        setQuestions(res.data);
        setLoading(false);
      } catch (err) {
        setError('Failed to load questions.');
        setLoading(false);
      }
    };
    fetchQuestions();
  }, []);

  const handleAnswer = (answer) => {
    setAnswers([...answers, { questionId: questions[current]._id, answer }]);
    if (current < questions.length - 1) {
      setCurrent(current + 1);
    } else {
      localStorage.setItem('quizAnswers', JSON.stringify(answers.concat({ questionId: questions[current]._id, answer })));
      navigate('/results');
    }
  };

  if (loading) return <div>Loading questions...</div>;
  if (error) return <div style={{ color: 'red' }}>{error}</div>;
  if (!questions.length) return <div>No questions found.</div>;

  const q = questions[current];

  return (
    <div>
      <h2>Question {current + 1} of {questions.length}</h2>
      <div>{q.question}</div>
      <div>
        {q.options && q.options.map((opt, idx) => (
          <button key={idx} onClick={() => handleAnswer(opt)} style={{ display: 'block', margin: '8px 0' }}>{opt}</button>
        ))}
      </div>
    </div>
  );
}

export default Quiz;
