import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [page, setPage] = useState('home');
  const [token, setToken] = useState(localStorage.getItem('token') || '');
  const [quizAnswers, setQuizAnswers] = useState([]);
  const [score, setScore] = useState(null);
  const [questions, setQuestions] = useState([]);
  const [current, setCurrent] = useState(0);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [registerError, setRegisterError] = useState('');
  const [loginError, setLoginError] = useState('');
  const [lastScore, setLastScore] = useState(null);
  const [review, setReview] = useState([]);
  const [timeLeft, setTimeLeft] = useState(15 * 60); // 15 minutes in seconds
  const [answerLocked, setAnswerLocked] = useState(false);
  const [lastWasCorrect, setLastWasCorrect] = useState(null); // null, true, or false
  const [showCorrect, setShowCorrect] = useState(false);
  const [lastCorrectAnswer, setLastCorrectAnswer] = useState('');
  const timerRef = useRef();

  // Handle login state
  useEffect(() => {
    if (token) localStorage.setItem('token', token);
    else localStorage.removeItem('token');
  }, [token]);

  // Dashboard: get last score
  useEffect(() => {
    const answers = JSON.parse(localStorage.getItem('quizAnswers')) || [];
    if (answers.length) {
      setLastScore(answers.filter(a => a.isCorrect).length + ' / ' + answers.length);
    }
  }, [page]);

  // Register
  const handleRegister = async (e) => {
    e.preventDefault();
    setRegisterError('');
    try {
      await axios.post('http://localhost:5000/api/auth/register', { username, password });
      setPage('login');
    } catch (err) {
      setRegisterError(err.response?.data?.message || 'Registration failed');
    }
  };

  // Login
  const handleLogin = async (e) => {
    e.preventDefault();
    setLoginError('');
    try {
      const res = await axios.post('http://localhost:5000/api/auth/login', { username, password });
      setToken(res.data.token);
      setPage('dashboard');
    } catch (err) {
      setLoginError(err.response?.data?.message || 'Login failed');
    }
  };

  // Start Quiz
  const startQuiz = async () => {
    setLoading(true);
    setError('');
    setQuizAnswers([]);
    setCurrent(0);
    try {
      const res = await axios.get('http://localhost:5000/api/questions?limit=50', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setQuestions(res.data);
      setLoading(false);
      setPage('quiz');
    } catch (err) {
      setError('Failed to load questions.');
      setLoading(false);
    }
  };

  // Timer logic
  useEffect(() => {
    if (page !== 'quiz' || loading) return;
    timerRef.current = setInterval(() => {
      setTimeLeft((prev) => {
        if (prev <= 1) {
          clearInterval(timerRef.current);
          localStorage.setItem('quizAnswers', JSON.stringify(quizAnswers));
          setPage('results');
          checkResults(quizAnswers);
          return 0;
        }
        return prev - 1;
      });
    }, 1000);
    return () => clearInterval(timerRef.current);
    // eslint-disable-next-line
  }, [page, loading]);

  // Answer a question
  const handleAnswer = (answer) => {
    if (answerLocked) return;
    setAnswerLocked(true);
    const isCorrect = answer === questions[current].correctAnswer;
    setLastWasCorrect(isCorrect);
    setShowCorrect(true);
    setLastCorrectAnswer(questions[current].correctAnswer);
    const newAnswers = [...quizAnswers, { questionId: questions[current]._id, answer }];
    setQuizAnswers(newAnswers);
    // Move to next after delay
    setTimeout(() => {
      setShowCorrect(false);
      setAnswerLocked(false);
      setLastWasCorrect(null);
      if (current < questions.length - 1) {
        setCurrent(current + 1);
      } else {
        localStorage.setItem('quizAnswers', JSON.stringify(newAnswers));
        setPage('results');
        checkResults(newAnswers);
      }
    }, 1500);
  };

  // Check results
  const checkResults = async (answers) => {
    setLoading(true);
    setReview([]);
    let correct = 0;
    let reviewArr = [];
    try {
      for (const ans of answers) {
        const res = await axios.post('http://localhost:5000/api/questions/submit', {
          questionId: ans.questionId,
          answer: ans.answer
        }, {
          headers: { Authorization: `Bearer ${token}` }
        });
        reviewArr.push({
          question: res.data.question,
          userAnswer: ans.answer,
          isCorrect: res.data.isCorrect,
          correctAnswer: res.data.correctAnswer
        });
        if (res.data.isCorrect) correct++;
      }
      setScore(correct);
      setReview(reviewArr);
      setLoading(false);
    } catch (err) {
      setError('Failed to calculate results.');
      setLoading(false);
    }
  };

  // Logout
  const handleLogout = () => {
    setToken('');
    setUsername('');
    setPassword('');
    setPage('home');
  };

  // UI
  return (
    <div className="app-container">
      {page === 'home' && (
        <div style={{ textAlign: 'center' }}>
          <h1>Welcome to the Quiz App!</h1>
          <button onClick={() => setPage('register')}>Register</button>
          <button onClick={() => setPage('login')}>Login</button>
        </div>
      )}
      {page === 'register' && (
        <div>
          <h2>Register</h2>
          {registerError && <div className="error">{registerError}</div>}
          <form onSubmit={handleRegister}>
            <input type="text" placeholder="Username" value={username} onChange={e => setUsername(e.target.value)} required />
            <input type="password" placeholder="Password" value={password} onChange={e => setPassword(e.target.value)} required />
            <button type="submit">Register</button>
          </form>
          <p>Already have an account? <button onClick={() => setPage('login')}>Login</button></p>
        </div>
      )}
      {page === 'login' && (
        <div>
          <h2>Login</h2>
          {loginError && <div className="error">{loginError}</div>}
          <form onSubmit={handleLogin}>
            <input type="text" placeholder="Username" value={username} onChange={e => setUsername(e.target.value)} required />
            <input type="password" placeholder="Password" value={password} onChange={e => setPassword(e.target.value)} required />
            <button type="submit">Login</button>
          </form>
          <p>Don't have an account? <button onClick={() => setPage('register')}>Register</button></p>
        </div>
      )}
      {page === 'dashboard' && (
        <div>
          <h2>Dashboard</h2>
          <p>Welcome, {username || 'User'}!</p>
          {lastScore && <p>Last Score: {lastScore}</p>}
          <button onClick={startQuiz}>Start New Quiz</button>
          <button onClick={handleLogout} style={{ background: '#d32f2f', marginLeft: 8 }}>Logout</button>
        </div>
      )}
      {page === 'quiz' && (
        <div>
          {/* --- PROMINENT TIMER BAR --- */}
          <div style={{ width: '100%', display: 'flex', justifyContent: 'center', alignItems: 'center', margin: '24px 0' }}>
            <div style={{
              fontWeight: 'bold',
              fontSize: 36,
              color: '#fff',
              background: timeLeft < 60 ? '#f44336' : '#2575fc',
              borderRadius: 12,
              padding: '16px 48px',
              boxShadow: '0 4px 16px #0002',
              letterSpacing: 2,
              border: '4px solid #fff',
              minWidth: 320,
              textAlign: 'center',
              transition: 'background 0.3s, color 0.3s'
            }}>
              ⏰ Time Left: {Math.floor(timeLeft / 60)}:{(timeLeft % 60).toString().padStart(2, '0')}
            </div>
          </div>
          <h2>Question {current + 1} of {questions.length}</h2>
          {loading ? <div>Loading questions...</div> : (
            <>
              <div>{questions[current]?.question}</div>
              <div>
                {questions[current]?.options && questions[current].options.map((opt, idx) => (
                  <button
                    key={idx}
                    onClick={() => handleAnswer(opt)}
                    style={{
                      display: 'block',
                      margin: '8px 0',
                      width: '100%',
                      background: quizAnswers[current]?.answer === opt
                        ? lastWasCorrect === false && opt === questions[current].correctAnswer
                          ? '#4caf50'
                          : lastWasCorrect === false && quizAnswers[current]?.answer === opt
                            ? '#f44336'
                            : lastWasCorrect === true && quizAnswers[current]?.answer === opt
                              ? '#4caf50'
                              : '#2575fc'
                        : '#fff',
                      color: quizAnswers[current]?.answer === opt ? '#fff' : '#222',
                      fontWeight: quizAnswers[current]?.answer === opt ? 'bold' : 'normal',
                      border: quizAnswers[current]?.answer === opt
                        ? lastWasCorrect === false && opt === questions[current].correctAnswer
                          ? '3px solid #4caf50'
                          : lastWasCorrect === false && quizAnswers[current]?.answer === opt
                            ? '3px solid #f44336'
                            : lastWasCorrect === true && quizAnswers[current]?.answer === opt
                              ? '3px solid #4caf50'
                              : '2px solid #2575fc'
                        : '1px solid #ccc',
                      borderRadius: 8,
                      fontSize: 20,
                      transition: 'background 0.3s, border 0.3s, color 0.3s'
                    }}
                    disabled={answerLocked}
                  >
                    {opt}
                  </button>
                ))}
              </div>
              {showCorrect && lastWasCorrect === false && (
                <div style={{ color: 'green', marginTop: 8 }}>
                  Correct answer: {lastCorrectAnswer}
                </div>
              )}
              {showCorrect && lastWasCorrect === true && (
                <div style={{ color: 'blue', marginTop: 8 }}>
                  Correct!
                </div>
              )}
            </>
          )}
        </div>
      )}
      {page === 'results' && (
        <div>
          <h2>Quiz Results</h2>
          {loading ? <div>Calculating results...</div> : (
            <>
              <p>Your score: {score} out of {quizAnswers.length}</p>
              <h3>Review</h3>
              {review.map((item, idx) => (
                <div key={idx} className="review">
                  <div><b>Q{idx + 1}:</b> {item.question}</div>
                  <div>Your answer: <span className={item.isCorrect ? 'correct' : 'incorrect'}>{item.userAnswer}</span></div>
                  {!item.isCorrect && <div>Correct answer: <span className="correct">{item.correctAnswer}</span></div>}
                </div>
              ))}
              <button onClick={() => setPage('dashboard')}>Go to Dashboard</button>
            </>
          )}
        </div>
      )}
    </div>
  );
}

export default App;
