import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [page, setPage] = useState('home');
  const [token, setToken] = useState(localStorage.getItem('token') || '');
  const [quizAnswers, setQuizAnswers] = useState([]);
  const [score, setScore] = useState(null);
  const [questions, setQuestions] = useState([]);
  const [current, setCurrent] = useState(0);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [registerError, setRegisterError] = useState('');
  const [loginError, setLoginError] = useState('');
  const [lastScore, setLastScore] = useState(null);
  const [review, setReview] = useState([]);
  const [timeLeft, setTimeLeft] = useState(15 * 60); // 15 minutes in seconds
  const [answerLocked, setAnswerLocked] = useState(false);
  const [lastWasCorrect, setLastWasCorrect] = useState(null);
  const [showCorrect, setShowCorrect] = useState(false);
  const [lastCorrectAnswer, setLastCorrectAnswer] = useState('');
  const [animating, setAnimating] = useState(false);
  const timerRef = useRef();

  // Handle login state
  useEffect(() => {
    if (token) localStorage.setItem('token', token);
    else localStorage.removeItem('token');
  }, [token]);

  // Dashboard: get last score
  useEffect(() => {
    const answers = JSON.parse(localStorage.getItem('quizAnswers')) || [];
    if (answers.length) {
      setLastScore(answers.filter(a => a.isCorrect).length + ' / ' + answers.length);
    }
  }, [page]);

  // Register
  const handleRegister = async (e) => {
    e.preventDefault();
    setRegisterError('');
    try {
      await axios.post('http://localhost:5000/api/auth/register', { username, password });
      setPage('login');
    } catch (err) {
      setRegisterError(err.response?.data?.message || 'Registration failed');
    }
  };

  // Login
  const handleLogin = async (e) => {
    e.preventDefault();
    setLoginError('');
    try {
      const res = await axios.post('http://localhost:5000/api/auth/login', { username, password });
      setToken(res.data.token);
      setPage('dashboard');
    } catch (err) {
      setLoginError(err.response?.data?.message || 'Login failed');
    }
  };

  // Start Quiz
  const startQuiz = async () => {
    setLoading(true);
    setError('');
    setQuizAnswers([]);
    setCurrent(0);
    try {
      const res = await axios.get('http://localhost:5000/api/questions?limit=50', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setQuestions(res.data);
      setLoading(false);
      setPage('quiz');
    } catch (err) {
      setError('Failed to load questions.');
      setLoading(false);
    }
  };

  // Timer logic
  useEffect(() => {
    if (page !== 'quiz' || loading) return;
    timerRef.current = setInterval(() => {
      setTimeLeft((prev) => {
        if (prev <= 1) {
          clearInterval(timerRef.current);
          localStorage.setItem('quizAnswers', JSON.stringify(quizAnswers));
          setPage('results');
          checkResults(quizAnswers);
          return 0;
        }
        return prev - 1;
      });
    }, 1000);
    return () => clearInterval(timerRef.current);
  }, [page, loading]);

  // Answer a question
  const handleAnswer = (answer) => {
    if (answerLocked) return;
    setAnswerLocked(true);
    const isCorrect = answer === questions[current].correctAnswer;
    setLastWasCorrect(isCorrect);
    setShowCorrect(true);
    setLastCorrectAnswer(questions[current].correctAnswer);
    const newAnswers = [...quizAnswers, { questionId: questions[current]._id, answer }];
    setQuizAnswers(newAnswers);
    
    setTimeout(() => {
      setShowCorrect(false);
      setAnswerLocked(false);
      setLastWasCorrect(null);
      if (current < questions.length - 1) {
        setCurrent(current + 1);
      } else {
        localStorage.setItem('quizAnswers', JSON.stringify(newAnswers));
        setPage('results');
        checkResults(newAnswers);
      }
    }, 1500);
  };

  // Check results
  const checkResults = async (answers) => {
    setLoading(true);
    setReview([]);
    let correct = 0;
    let reviewArr = [];
    try {
      for (const ans of answers) {
        const res = await axios.post('http://localhost:5000/api/questions/submit', {
          questionId: ans.questionId,
          answer: ans.answer
        }, {
          headers: { Authorization: `Bearer ${token}` }
        });
        reviewArr.push({
          question: res.data.question,
          userAnswer: ans.answer,
          isCorrect: res.data.isCorrect,
          correctAnswer: res.data.correctAnswer
        });
        if (res.data.isCorrect) correct++;
      }
      setScore(correct);
      setReview(reviewArr);
      setLoading(false);
    } catch (err) {
      setError('Failed to calculate results.');
      setLoading(false);
    }
  };

  // Logout
  const handleLogout = () => {
    setToken('');
    setUsername('');
    setPassword('');
    setPage('home');
  };

  // Navigation
  const handleNext = () => {
    if (current < questions.length - 1) {
      setAnimating(true);
      setTimeout(() => {
        setCurrent(current + 1);
        setAnimating(false);
      }, 300);
    }
  };

  const handlePrev = () => {
    if (current > 0) {
      setAnimating(true);
      setTimeout(() => {
        setCurrent(current - 1);
        setAnimating(false);
      }, 300);
    }
  };

  // Format time
  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins}:${secs < 10 ? '0' : ''}${secs}`;
  };

  // Calculate progress
  const progress = ((current + 1) / questions.length) * 100;

  return (
    <div className="app-container">
      {page === 'home' && (
        <div>
          <h1>Welcome to the Quiz App!</h1>
          <div className="options">
            <button onClick={() => setPage('register')}>Register</button>
            <button onClick={() => setPage('login')}>Login</button>
          </div>
        </div>
      )}

      {page === 'register' && (
        <div>
          <h2>Register</h2>
          {registerError && <div className="error">{registerError}</div>}
          <form onSubmit={handleRegister} className="login-form">
            <input
              type="text"
              placeholder="Username"
              value={username}
              onChange={e => setUsername(e.target.value)}
              required
            />
            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={e => setPassword(e.target.value)}
              required
            />
            <button type="submit">Register</button>
          </form>
          <p style={{ textAlign: 'center', marginTop: '20px' }}>
            Already have an account? <button onClick={() => setPage('login')}>Login</button>
          </p>
        </div>
      )}

      {page === 'login' && (
        <div>
          <h2>Login</h2>
          {loginError && <div className="error">{loginError}</div>}
          <form onSubmit={handleLogin} className="login-form">
            <input
              type="text"
              placeholder="Username"
              value={username}
              onChange={e => setUsername(e.target.value)}
              required
            />
            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={e => setPassword(e.target.value)}
              required
            />
            <button type="submit">Login</button>
          </form>
          <p style={{ textAlign: 'center', marginTop: '20px' }}>
            Don't have an account? <button onClick={() => setPage('register')}>Register</button>
          </p>
        </div>
      )}

      {page === 'dashboard' && (
        <div>
          <h2>Dashboard</h2>
          <p style={{ textAlign: 'center', marginBottom: '20px' }}>Welcome, {username || 'User'}!</p>
          {lastScore && <p style={{ textAlign: 'center', marginBottom: '20px' }}>Last Score: {lastScore}</p>}
          <div className="options">
            <button onClick={startQuiz}>Start New Quiz</button>
            <button onClick={handleLogout} style={{ background: '#ff1744' }}>Logout</button>
          </div>
        </div>
      )}

      {page === 'quiz' && (
        <div>
          <h2>General Knowledge Quiz</h2>
          <div className="timer">Time Left: {formatTime(timeLeft)}</div>
          <div className="question">
            {current + 1}. {questions[current]?.question}
          </div>
          <div className="options">
            {questions[current]?.options.map((option, idx) => (
              <button
                key={idx}
                onClick={() => handleAnswer(option)}
                disabled={answerLocked}
                className={`
                  ${answerLocked && option === questions[current].correctAnswer ? 'correct' : ''}
                  ${answerLocked && option === quizAnswers[current]?.answer && option !== questions[current].correctAnswer ? 'wrong' : ''}
                `}
              >
                {option}
              </button>
            ))}
          </div>
          {showCorrect && !lastWasCorrect && (
            <div className="feedback">
              Correct answer: {lastCorrectAnswer}
            </div>
          )}
          <div className="navigation">
            <button className="nav-btn" onClick={handlePrev} disabled={current === 0 || answerLocked}>
              Previous
            </button>
            <button
              className="nav-btn"
              onClick={handleNext}
              disabled={current === questions.length - 1 || answerLocked}
            >
              Next
            </button>
          </div>
          <div className="score">Score: {quizAnswers.filter(a => a.isCorrect).length}/{questions.length}</div>
          <div className="progress-bar">
            <div className="progress" style={{ width: `${progress}%` }}></div>
          </div>
        </div>
      )}

      {page === 'results' && (
        <div>
          <h2>Quiz Results</h2>
          {loading ? (
            <div>Calculating results...</div>
          ) : (
            <>
              <div className="score score-update">
                Your score: {score} out of {quizAnswers.length}
              </div>
              <h3>Review</h3>
              {review.map((item, idx) => (
                <div key={idx} className="question" style={{ marginBottom: '20px' }}>
                  <div><b>Q{idx + 1}:</b> {item.question}</div>
                  <div>Your answer: <span className={item.isCorrect ? 'correct' : 'wrong'}>{item.userAnswer}</span></div>
                  {!item.isCorrect && (
                    <div className="feedback">Correct answer: {item.correctAnswer}</div>
                  )}
                </div>
              ))}
              <div className="options">
                <button onClick={() => setPage('dashboard')}>Go to Dashboard</button>
              </div>
            </>
          )}
        </div>
      )}
    </div>
  );
}

export default App;
